import React, { useState, useEffect } from 'react';
import { initializeApp } from 'firebase/app';
import { getAuth, signInAnonymously, signInWithCustomToken, onAuthStateChanged } from 'firebase/auth';
import { getFirestore, collection, onSnapshot, addDoc, setDoc, deleteDoc, doc, query } from 'firebase/firestore';

// Main App component
const App = () => {
  // State for Firebase instances and user ID
  const [db, setDb] = useState(null);
  const [auth, setAuth] = useState(null);
  const [userId, setUserId] = useState(null);
  const [isAuthReady, setIsAuthReady] = useState(false);

  // State to store the list of food items
  const [foodItems, setFoodItems] = useState([]);
  // State for the form inputs
  const [foodName, setFoodName] = useState('');
  const [foodImage, setFoodImage] = useState('');
  const [foodTags, setFoodTags] = useState('');
  // State to track if we are in edit mode
  const [editingFoodId, setEditingFoodId] = useState(null);
  // State for showing success/error messages
  const [message, setMessage] = useState({ text: '', type: '' });
  // State for loading indicator
  const [isLoading, setIsLoading] = useState(true);
  // State for confirmation modal
  const [showConfirmModal, setShowConfirmModal] = useState(false);
  const [itemToDelete, setItemToDelete] = useState(null);

  // Initialize Firebase and set up authentication
  useEffect(() => {
    try {
      // Get app ID and Firebase config from global variables
      const appId = typeof __app_id !== 'undefined' ? __app_id : 'default-app-id';
      const firebaseConfig = typeof __firebase_config !== 'undefined' ? JSON.parse(__firebase_config) : {};

      // Initialize Firebase
      const app = initializeApp(firebaseConfig);
      const firestoreDb = getFirestore(app);
      const firebaseAuth = getAuth(app);

      setDb(firestoreDb);
      setAuth(firebaseAuth);

      // Listen for auth state changes
      const unsubscribe = onAuthStateChanged(firebaseAuth, async (user) => {
        if (user) {
          setUserId(user.uid);
        } else {
          // Sign in anonymously if no user is found
          try {
            if (typeof __initial_auth_token !== 'undefined') {
              await signInWithCustomToken(firebaseAuth, __initial_auth_token);
            } else {
              await signInAnonymously(firebaseAuth);
            }
            setUserId(firebaseAuth.currentUser?.uid || crypto.randomUUID()); // Fallback for anonymous user ID
          } catch (error) {
            console.error("Firebase Auth Error:", error);
            showMessage('เกิดข้อผิดพลาดในการเข้าสู่ระบบ Firebase', 'error');
          }
        }
        setIsAuthReady(true); // Mark auth as ready
      });

      return () => unsubscribe(); // Cleanup auth listener on unmount
    } catch (error) {
      console.error("Firebase Initialization Error:", error);
      showMessage('เกิดข้อผิดพลาดในการเริ่มต้น Firebase', 'error');
    }
  }, []);

  // Effect to fetch food items from Firestore in real-time
  useEffect(() => {
    if (db && userId && isAuthReady) {
      setIsLoading(true);
      // Define the collection path based on security rules (public data)
      const appId = typeof __app_id !== 'undefined' ? __app_id : 'default-app-id';
      const foodCollectionRef = collection(db, `artifacts/${appId}/public/data/thaiFoodMenu`);
      const q = query(foodCollectionRef); // Create a query

      const unsubscribe = onSnapshot(q, (snapshot) => {
        const items = snapshot.docs.map(doc => ({
          id: doc.id,
          ...doc.data()
        }));
        setFoodItems(items);
        setIsLoading(false);
      }, (error) => {
        console.error("Error fetching food items from Firestore:", error);
        showMessage('เกิดข้อผิดพลาดในการดึงข้อมูลเมนูอาหาร', 'error');
        setIsLoading(false);
      });

      return () => unsubscribe(); // Cleanup listener on unmount
    }
  }, [db, userId, isAuthReady]); // Re-run when db, userId, or isAuthReady changes

  // Function to handle adding or updating a food item
  const handleAddOrUpdateFood = async (e) => {
    e.preventDefault(); // Prevent default form submission

    if (!db || !userId) {
      showMessage('Firebase ยังไม่พร้อมใช้งาน กรุณารอสักครู่', 'error');
      return;
    }

    // Basic validation
    if (!foodName.trim() || !foodImage.trim()) {
      showMessage('กรุณากรอกชื่อและรูปภาพอาหาร', 'error');
      return;
    }

    const tagsArray = foodTags.split(',').map(tag => tag.trim()).filter(tag => tag !== '');
    setIsLoading(true);

    try {
      const appId = typeof __app_id !== 'undefined' ? __app_id : 'default-app-id';
      const foodCollectionRef = collection(db, `artifacts/${appId}/public/data/thaiFoodMenu`);

      if (editingFoodId) {
        // Update existing food item
        const foodDocRef = doc(db, `artifacts/${appId}/public/data/thaiFoodMenu`, editingFoodId);
        await setDoc(foodDocRef, { name: foodName, image: foodImage, tags: tagsArray }, { merge: true });
        showMessage('อัปเดตเมนูอาหารเรียบร้อยแล้ว!', 'success');
        setEditingFoodId(null); // Exit edit mode
      } else {
        // Add new food item
        await addDoc(foodCollectionRef, {
          name: foodName,
          image: foodImage,
          tags: tagsArray,
        });
        showMessage('เพิ่มเมนูอาหารเรียบร้อยแล้ว!', 'success');
      }

      // Clear form fields
      setFoodName('');
      setFoodImage('');
      setFoodTags('');
    } catch (error) {
      console.error("Error adding/updating food item:", error);
      showMessage('เกิดข้อผิดพลาดในการเพิ่ม/อัปเดตเมนูอาหาร', 'error');
    } finally {
      setIsLoading(false);
    }
  };

  // Function to handle editing a food item
  const handleEditFood = (id) => {
    const foodToEdit = foodItems.find(item => item.id === id);
    if (foodToEdit) {
      setFoodName(foodToEdit.name);
      setFoodImage(foodToEdit.image);
      setFoodTags(foodToEdit.tags.join(', '));
      setEditingFoodId(id); // Set the ID of the item being edited
      showMessage('กำลังแก้ไขเมนูอาหาร...', 'info');
    }
  };

  // Function to handle deleting a food item
  const handleDeleteFood = (id) => {
    setItemToDelete(id);
    setShowConfirmModal(true);
  };

  // Function to confirm deletion
  const confirmDelete = async () => {
    setShowConfirmModal(false);
    if (!db || !itemToDelete) return;

    setIsLoading(true);
    try {
      const appId = typeof __app_id !== 'undefined' ? __app_id : 'default-app-id';
      const foodDocRef = doc(db, `artifacts/${appId}/public/data/thaiFoodMenu`, itemToDelete);
      await deleteDoc(foodDocRef);
      showMessage('ลบเมนูอาหารเรียบร้อยแล้ว!', 'success');
    } catch (error) {
      console.error("Error deleting food item:", error);
      showMessage('เกิดข้อผิดพลาดในการลบเมนูอาหาร', 'error');
    } finally {
      setIsLoading(false);
      setItemToDelete(null);
    }
  };

  // Function to display messages
  const showMessage = (text, type) => {
    setMessage({ text, type });
    setTimeout(() => {
      setMessage({ text: '', type: '' }); // Clear message after 3 seconds
    }, 3000);
  };

  return (
    <div className="min-h-screen p-4 bg-gradient-to-br from-orange-50 to-amber-100 font-inter">
      {/* Tailwind CSS CDN */}
      <script src="https://cdn.tailwindcss.com"></script>
      {/* Header Section */}
      <header className="mb-8 text-center">
        <h1 className="mb-2 text-5xl font-extrabold text-amber-800 drop-shadow-lg">
          คลังอาหารของ ODG
        </h1>
        <p className="text-xl text-amber-600">จัดการรายการเมนูอาหารที่คุณชื่นชอบ</p>
        {userId && (
          <p className="mt-2 text-sm text-gray-500">
            รหัสผู้ใช้: <span className="font-mono text-gray-700">{userId}</span>
          </p>
        )}
      </header>

      {/* Message Display */}
      {message.text && (
        <div
          className={`fixed top-4 right-4 z-50 p-4 rounded-lg shadow-xl text-white font-semibold transition-all duration-300 transform ${
            message.type === 'success' ? 'bg-green-500' :
            message.type === 'error' ? 'bg-red-500' : 'bg-blue-500'
          }`}
        >
          <div>
            <span>{message.text}</span>
          </div>
        </div>
      )}

      {/* Loading Indicator */}
      {isLoading && (
        <div className="fixed inset-0 z-50 flex items-center justify-center bg-black bg-opacity-50">
          <div className="flex items-center px-6 py-4 text-lg font-semibold text-white rounded-lg shadow-lg bg-amber-600">
            <svg className="w-6 h-6 mr-3 animate-spin" fill="none" viewBox="0 0 24 24">
              <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
              <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
            </svg>
            กำลังโหลด...
          </div>
        </div>
      )}

      {/* Confirmation Modal */}
      {showConfirmModal && (
        <div className="fixed inset-0 z-50 flex items-center justify-center bg-black bg-opacity-50">
          <div className="p-8 bg-white rounded-lg shadow-xl">
            <h3 className="mb-4 text-xl font-bold text-gray-800">ยืนยันการลบ</h3>
            <p className="mb-6 text-gray-700">คุณแน่ใจหรือไม่ว่าต้องการลบเมนูอาหารนี้?</p>
            <div className="flex justify-end space-x-4">
              <button
                onClick={() => setShowConfirmModal(false)}
                className="px-6 py-2 font-semibold text-gray-700 bg-gray-200 rounded-lg hover:bg-gray-300"
              >
                ยกเลิก
              </button>
              <button
                onClick={confirmDelete}
                className="px-6 py-2 font-semibold text-white bg-red-500 rounded-lg hover:bg-red-600"
              >
                ลบ
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Food Item Form */}
      <section className="max-w-3xl p-8 mx-auto mb-10 bg-white border shadow-xl rounded-2xl border-amber-200">
        <h2 className="mb-6 text-3xl font-bold text-center text-amber-700">
          {editingFoodId ? 'แก้ไขเมนูอาหาร' : 'เพิ่มเมนูอาหารใหม่'}
        </h2>
        <form onSubmit={handleAddOrUpdateFood} className="space-y-5">
          <div>
            <label htmlFor="foodName" className="block mb-2 text-lg font-medium text-gray-700">
              ชื่ออาหาร:
            </label>
            <input
              type="text"
              id="foodName"
              value={foodName}
              onChange={(e) => setFoodName(e.target.value)}
              className="block w-full px-4 py-3 mt-1 text-lg border border-gray-300 shadow-sm rounded-xl focus:ring-amber-500 focus:border-amber-500"
              placeholder="เช่น ผัดไทย, ต้มยำกุ้ง"
              required
            />
          </div>
          <div>
            <label htmlFor="foodImage" className="block mb-2 text-lg font-medium text-gray-700">
              URL รูปภาพ:
            </label>
            <input
              type="url"
              id="foodImage"
              value={foodImage}
              onChange={(e) => setFoodImage(e.target.value)}
              className="block w-full px-4 py-3 mt-1 text-lg border border-gray-300 shadow-sm rounded-xl focus:ring-amber-500 focus:border-amber-500"
              placeholder="https://example.com/padthai.jpg"
              required
            />
          </div>
          <div>
            <label htmlFor="foodTags" className="block mb-2 text-lg font-medium text-gray-700">
              แท็ก (คั่นด้วยคอมม่า):
            </label>
            <input
              type="text"
              id="foodTags"
              value={foodTags}
              onChange={(e) => setFoodTags(e.target.value)}
              className="block w-full px-4 py-3 mt-1 text-lg border border-gray-300 shadow-sm rounded-xl focus:ring-amber-500 focus:border-amber-500"
              placeholder="เช่น อาหารจานเดียว, เส้น, ยอดนิยม"
            />
          </div>
          <div className="flex justify-center space-x-4">
            <button
              type="submit"
              className="px-8 py-3 font-bold text-white transition duration-300 ease-in-out transform shadow-lg bg-amber-600 rounded-xl hover:bg-amber-700 focus:outline-none focus:ring-2 focus:ring-amber-500 focus:ring-offset-2 hover:scale-105"
            >
              {editingFoodId ? 'บันทึกการแก้ไข' : 'เพิ่มเมนูอาหาร'}
            </button>
            {editingFoodId && (
              <button
                type="button"
                onClick={() => {
                  setEditingFoodId(null);
                  setFoodName('');
                  setFoodImage('');
                  setFoodTags('');
                  showMessage('ยกเลิกการแก้ไข', 'info');
                }}
                className="px-8 py-3 font-bold text-white transition duration-300 ease-in-out transform bg-gray-400 shadow-lg rounded-xl hover:bg-gray-500 focus:outline-none focus:ring-2 focus:ring-gray-300 focus:ring-offset-2 hover:scale-105"
              >
                ยกเลิก
              </button>
            )}
          </div>
        </form>
      </section>

      {/* Food Items Grid */}
      <section className="max-w-6xl mx-auto">
        <h2 className="mb-6 text-3xl font-bold text-center text-amber-700">รายการเมนูอาหาร</h2>
        {foodItems.length === 0 && !isLoading ? (
          <p className="mt-8 text-xl text-center text-gray-600">
            ยังไม่มีเมนูอาหารในรายการ ลองเพิ่มเมนูใหม่ดูสิ!
          </p>
        ) : (
          <div className="grid grid-cols-1 gap-8 sm:grid-cols-2 md:grid-cols-2 lg:grid-cols-2">
            {foodItems.map((food) => (
              <div
                key={food.id}
                className="overflow-hidden transition duration-300 ease-in-out transform bg-white border shadow-xl rounded-2xl border-amber-200 hover:scale-105"
              >
                <img
                  src={food.image}
                  alt={food.name}
                  className="object-cover w-full h-48 rounded-t-2xl"
                  onError={(e) => {
                    e.target.onerror = null; // Prevent infinite loop
                    e.target.src = `https://placehold.co/400x300/CCCCCC/000000?text=Image+Not+Found`; // Placeholder on error
                  }}
                />
                <div className="p-6">
                  <h3 className="mb-3 text-2xl font-semibold text-gray-800">{food.name}</h3>
                  <div className="flex flex-wrap gap-2 mb-4">
                    {food.tags.map((tag, index) => (
                      <span
                        key={index}
                        className="px-3 py-1 text-sm font-medium rounded-full shadow-md bg-amber-100 text-amber-700"
                      >
                        {tag}
                      </span>
                    ))}
                  </div>
                  <div className="flex justify-end space-x-3">
                    <button
                      onClick={() => handleEditFood(food.id)}
                      className="px-5 py-2 text-white transition duration-200 ease-in-out bg-blue-500 rounded-lg shadow-md hover:bg-blue-600 focus:outline-none focus:ring-2 focus:ring-blue-400 focus:ring-offset-2"
                    >
                      แก้ไข
                    </button>
                    <button
                      onClick={() => handleDeleteFood(food.id)}
                      className="px-5 py-2 text-white transition duration-200 ease-in-out bg-red-500 rounded-lg shadow-md hover:bg-red-600 focus:outline-none focus:ring-2 focus:ring-red-400 focus:ring-offset-2"
                    >
                      ลบ
                    </button>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </section>
    </div>
  );
};

export default App;
