import React, { useState, useEffect } from 'react';

// Main App component
const App = () => {
  // State for current page view: 'my_foods', 'vote', 'admin'
  const [currentPage, setCurrentPage] = useState('my_foods');

  // User ID for offline voting (persists per browser session)
  const [userId, setUserId] = useState(() => {
    let id = localStorage.getItem('offlineUserId');
    if (!id) {
      id = crypto.randomUUID();
      localStorage.setItem('offlineUserId', id);
    }
    return id;
  });

  // Simple admin state for offline demo (not secure for real apps)
  const [isAdmin, setIsAdmin] = useState(false);
  const [adminPasswordInput, setAdminPasswordInput] = useState('');
  const ADMIN_PASSWORD = 'admin'; // Hardcoded admin password for demo

  // State to store the list of food items (user's private list)
  const [foodItems, setFoodItems] = useState(() => {
    const savedFoodItems = localStorage.getItem('thaiFoodMenu_foodItems');
    return savedFoodItems ? JSON.parse(savedFoodItems) : [
      { id: '1', name: 'ผัดไทย', image: 'https://placehold.co/400x300/FFD700/000000?text=Pad+Thai', tags: ['อาหารจานเดียว', 'เส้น', 'ยอดนิยม', 'ทั่วไป'] },
      { id: '2', name: 'ต้มยำกุ้ง', image: 'https://placehold.co/400x300/FF6347/FFFFFF?text=Tom+Yum+Goong', tags: ['ซุป', 'เผ็ด', 'อาหารทะเล', 'ทั่วไป'] },
      { id: '3', name: 'แกงเขียวหวาน', image: 'https://placehold.co/400x300/32CD32/FFFFFF?text=Green+Curry', tags: ['แกง', 'กะทิ', 'เผ็ดน้อย', 'ทั่วไป'] },
      { id: '4', name: 'ข้าวผัด', image: 'https://placehold.co/400x300/87CEEB/000000?text=Fried+Rice', tags: ['อาหารจานเดียว', 'ข้าว', 'ง่ายๆ', 'ทั่วไป'] },
      { id: '5', name: 'ส้มตำ', image: 'https://placehold.co/400x300/FF4500/FFFFFF?text=Som+Tum', tags: ['สลัด', 'เผ็ด', 'อีสาน', 'ทั่วไป'] },
      { id: '6', name: 'มัสมั่นไก่', image: 'https://placehold.co/400x300/8B4513/FFFFFF?text=Massaman+Curry', tags: ['แกง', 'กะทิ', 'ไม่เผ็ด', 'ทั่วไป'] },
      { id: '7', name: 'ราดหน้าหมู', image: 'https://placehold.co/400x300/90EE90/000000?text=Rad+Na+Moo', tags: ['อาหารจานเดียว', 'เส้น', 'อาหารราดหน้า'] },
      { id: '8', name: 'ก๋วยเตี๋ยวเรือ', image: 'https://placehold.co/400x300/DDA0DD/FFFFFF?text=Boat+Noodle', tags: ['อาหารจานเดียว', 'เส้น'] },
    ];
  });

  // State for the daily menu/voting status (public, stored in localStorage)
  const [dailyMenu, setDailyMenu] = useState(() => {
    const savedDailyMenu = localStorage.getItem('thaiFoodMenu_dailyMenu');
    return savedDailyMenu ? JSON.parse(savedDailyMenu) : {
      status: 'idle', // 'idle', 'voting', 'closed', 'admin_set'
      voteOptions: [],
      votedUsers: {}, // { userId: foodItemId }
      winningFoodItemId: null,
      adminSetFoodItemId: null,
      timestamp: null,
    };
  });

  // State for the form inputs (for adding/editing food items)
  const [foodName, setFoodName] = useState('');
  const [foodImage, setFoodImage] = useState('');
  const [foodTags, setFoodTags] = useState('');
  const [editingFoodId, setEditingFoodId] = useState(null);

  // State for admin panel: selected items for voting (stores full food objects)
  const [adminVoteSelections, setAdminVoteSelections] = useState([]);
  const [adminDirectSelectFoodId, setAdminDirectSelectFoodId] = useState('');

  // State for filtering food items in 'my_foods' page
  const [selectedCategory, setSelectedCategory] = useState('ทั้งหมด'); // 'ทั้งหมด', 'อาหารราดหน้า', 'ทั่วไป', 'เส้น'
  // State for filtering food items in 'admin' page
  const [selectedAdminCategory, setSelectedAdminCategory] = useState('ทั้งหมด'); // 'ทั้งหมด', 'อาหารราดหน้า', 'ทั่วไป', 'เส้น'

  // State for showing success/error messages
  const [message, setMessage] = useState({ text: '', type: '' });

  // --- Effects to save data to Local Storage ---
  useEffect(() => {
    localStorage.setItem('thaiFoodMenu_foodItems', JSON.stringify(foodItems));
  }, [foodItems]);

  useEffect(() => {
    localStorage.setItem('thaiFoodMenu_dailyMenu', JSON.stringify(dailyMenu));
  }, [dailyMenu]);

  // --- CRUD Operations for Food Items (using localStorage) ---
  const handleAddOrUpdateFood = (e) => {
    e.preventDefault();

    if (!foodName.trim() || !foodImage.trim()) {
      showMessage('กรุณากรอกชื่อและรูปภาพอาหาร', 'error');
      return;
    }

    const tagsArray = foodTags.split(',').map(tag => tag.trim()).filter(tag => tag !== '');

    if (editingFoodId) {
      // Update existing food item
      setFoodItems(foodItems.map(item =>
        item.id === editingFoodId
          ? { ...item, name: foodName, image: foodImage, tags: tagsArray }
          : item
      ));
      showMessage('อัปเดตเมนูอาหารเรียบร้อยแล้ว!', 'success');
      setEditingFoodId(null);
    } else {
      // Add new food item
      const newFoodItem = {
        id: Date.now().toString(), // Unique ID based on timestamp
        name: foodName,
        image: foodImage,
        tags: tagsArray,
      };
      setFoodItems([...foodItems, newFoodItem]);
      showMessage('เพิ่มเมนูอาหารเรียบร้อยแล้ว!', 'success');
    }

    // Clear form fields
    setFoodName('');
    setFoodImage('');
    setFoodTags('');
  };

  const handleEditFood = (id) => {
    const foodToEdit = foodItems.find(item => item.id === id);
    if (foodToEdit) {
      setFoodName(foodToEdit.name);
      setFoodImage(foodToEdit.image);
      setFoodTags(foodToEdit.tags.join(', '));
      setEditingFoodId(id);
      showMessage('กำลังแก้ไขเมนูอาหาร...', 'info');
    }
  };

  const handleDeleteFood = (id) => {
    if (window.confirm('คุณต้องการลบเมนูอาหารนี้หรือไม่?')) {
      setFoodItems(foodItems.filter(item => item.id !== id));
      showMessage('ลบเมนูอาหารเรียบร้อยแล้ว!', 'success');
    }
  };

  // --- Admin Functions (using localStorage) ---
  const handleAdminLogin = () => {
    if (adminPasswordInput === ADMIN_PASSWORD) {
      setIsAdmin(true);
      showMessage('เข้าสู่ระบบแอดมินสำเร็จ!', 'success');
    } else {
      showMessage('รหัสผ่านแอดมินไม่ถูกต้อง', 'error');
    }
    setAdminPasswordInput('');
  };

  const handleAdminLogout = () => {
    setIsAdmin(false);
    showMessage('ออกจากระบบแอดมินแล้ว', 'info');
  };

  // Toggle selection for voting in admin panel
  const toggleAdminVoteSelection = (food) => {
    const isSelected = adminVoteSelections.some(item => item.id === food.id);
    if (isSelected) {
      setAdminVoteSelections(adminVoteSelections.filter(item => item.id !== food.id));
    } else {
      if (adminVoteSelections.length < 5) {
        setAdminVoteSelections([...adminVoteSelections, food]);
      } else {
        showMessage('เลือกได้สูงสุด 5 เมนูสำหรับโหวตเท่านั้น', 'info');
      }
    }
  };

  const handleStartVoting = () => {
    if (adminVoteSelections.length !== 5) {
      showMessage('ต้องเลือก 5 เมนูที่ไม่ซ้ำกันสำหรับโหวต', 'error');
      return;
    }

    const voteOptions = adminVoteSelections.map(item => ({
      foodItemId: item.id,
      name: item.name,
      image: item.image,
      votes: 0,
    }));

    setDailyMenu({
      status: 'voting',
      voteOptions: voteOptions,
      votedUsers: {}, // Reset voted users
      winningFoodItemId: null,
      adminSetFoodItemId: null,
      timestamp: new Date().toISOString(),
    });
    showMessage('เริ่มการโหวตเมนูประจำวันแล้ว!', 'success');
    setAdminVoteSelections([]); // Clear selected items after starting vote
  };

  const handleCloseVoting = () => {
    if (dailyMenu.status !== 'voting') {
      showMessage('ไม่ได้อยู่ในสถานะกำลังโหวต', 'error');
      return;
    }

    let winningItem = null;
    if (dailyMenu.voteOptions && dailyMenu.voteOptions.length > 0) {
      winningItem = dailyMenu.voteOptions.reduce((prev, current) =>
        (prev.votes > current.votes) ? prev : current
      );
    }

    setDailyMenu(prev => ({
      ...prev,
      status: 'closed',
      winningFoodItemId: winningItem ? winningItem.foodItemId : null,
      timestamp: new Date().toISOString(),
    }));
    showMessage(`ปิดการโหวตแล้ว! เมนูที่ชนะคือ ${winningItem ? winningItem.name : 'ไม่มี'}`, 'success');
  };

  const handleAdminSetFood = () => {
    if (!adminDirectSelectFoodId) {
      showMessage('ต้องเลือกเมนู', 'error');
      return;
    }

    setDailyMenu({
      status: 'admin_set',
      adminSetFoodItemId: adminDirectSelectFoodId,
      winningFoodItemId: null,
      voteOptions: [],
      votedUsers: {},
      timestamp: new Date().toISOString(),
    });
    showMessage('ตั้งค่าเมนูประจำวันโดยแอดมินเรียบร้อยแล้ว!', 'success');
    setAdminDirectSelectFoodId('');
  };

  // --- User Voting Function (using localStorage) ---
  const handleVote = (foodItemId) => {
    if (dailyMenu.status !== 'voting') {
      showMessage('ไม่สามารถโหวตได้ในขณะนี้', 'error');
      return;
    }

    // Check if user has already voted
    if (dailyMenu.votedUsers && dailyMenu.votedUsers[userId]) {
      showMessage('คุณโหวตไปแล้วสำหรับวันนี้!', 'info');
      return;
    }

    // Update the vote count for the selected item and mark user as voted
    const updatedVoteOptions = dailyMenu.voteOptions.map(option =>
      option.foodItemId === foodItemId
        ? { ...option, votes: option.votes + 1 }
        : option
    );

    setDailyMenu(prev => ({
      ...prev,
      voteOptions: updatedVoteOptions,
      votedUsers: {
        ...prev.votedUsers,
        [userId]: foodItemId, // Mark user as voted for this item
      },
      timestamp: new Date().toISOString(),
    }));
    showMessage('โหวตสำเร็จ!', 'success');
  };

  // Function to display messages
  const showMessage = (text, type) => {
    setMessage({ text, type });
    setTimeout(() => {
      setMessage({ text: '', type: '' }); // Clear message after 3 seconds
    }, 3000);
  };

  // Get winning food item details for display
  const getWinningFoodDetails = () => {
    if (!dailyMenu) return null;

    if (dailyMenu.status === 'closed' && dailyMenu.winningFoodItemId) {
      return foodItems.find(item => item.id === dailyMenu.winningFoodItemId) ||
             dailyMenu.voteOptions.find(option => option.foodItemId === dailyMenu.winningFoodItemId);
    } else if (dailyMenu.status === 'admin_set' && dailyMenu.adminSetFoodItemId) {
      return foodItems.find(item => item.id === dailyMenu.adminSetFoodItemId);
    }
    return null;
  };

  const winningFood = getWinningFoodDetails();

  // Filtered food items for 'my_foods' section
  const filteredFoodItems = foodItems.filter(food => {
    if (selectedCategory === 'ทั้งหมด') {
      return true;
    }
    return food.tags.includes(selectedCategory);
  });

  // Filtered food items for 'admin' selection section
  const filteredAdminFoodItems = foodItems.filter(food => {
    if (selectedAdminCategory === 'ทั้งหมด') {
      return true;
    }
    return food.tags.includes(selectedAdminCategory);
  });

  return (
    <div className="min-h-screen p-4 text-gray-800 bg-gradient-to-br from-emerald-50 to-teal-100 font-inter">
      {/* Header Section */}
      <header className="mb-8 text-center">
        <h1 className="mb-2 text-5xl font-extrabold text-teal-800 drop-shadow-lg">
          คลังอาหารของ ODG
        </h1>
        <p className="text-xl text-teal-600">จัดการรายการเมนูอาหารที่คุณชื่นชอบและโหวตเมนูประจำวัน</p>
        <p className="mt-2 text-sm text-gray-500">User ID: {userId}</p>
      </header>

      {/* Message Display */}
      {message.text && (
        <div
          className={`fixed top-4 right-4 z-50 p-4 rounded-lg shadow-xl text-white font-semibold transition-all duration-300 transform ${
            message.type === 'success' ? 'bg-green-500' :
            message.type === 'error' ? 'bg-red-500' : 'bg-blue-500'
          }`}
        >
          <div>
            <span>{message.text}</span>
          </div>
        </div>
      )}

      {/* Navigation Buttons */}
      <nav className="flex justify-center mb-8 space-x-4">
        <button
          onClick={() => setCurrentPage('my_foods')}
          className={`px-6 py-3 rounded-xl font-bold transition duration-300 ${
            currentPage === 'my_foods' ? 'bg-teal-600 text-white shadow-lg' : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
          }`}
        >
          เมนูส่วนตัว
        </button>
        <button
          onClick={() => setCurrentPage('vote')}
          className={`px-6 py-3 rounded-xl font-bold transition duration-300 ${
            currentPage === 'vote' ? 'bg-teal-600 text-white shadow-lg' : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
          }`}
        >
          โหวตเมนูประจำวัน
        </button>
        <button
          onClick={() => setCurrentPage('admin')}
          className={`px-6 py-3 rounded-xl font-bold transition duration-300 ${
            currentPage === 'admin' ? 'bg-teal-600 text-white shadow-lg' : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
          }`}
        >
          แผงควบคุมแอดมิน
        </button>
      </nav>

      {/* Conditional Rendering based on currentPage */}
      {currentPage === 'my_foods' && (
        <>
          {/* Food Item Form (for user's private list) */}
          <section className="max-w-3xl p-8 mx-auto mb-10 bg-white border border-teal-200 shadow-xl rounded-2xl">
            <h2 className="mb-6 text-3xl font-bold text-center text-teal-700">
              {editingFoodId ? 'แก้ไขเมนูอาหารส่วนตัว' : 'เพิ่มเมนูอาหารส่วนตัวใหม่'}
            </h2>
            <form onSubmit={handleAddOrUpdateFood} className="space-y-5">
              <div>
                <label htmlFor="foodName" className="block mb-2 text-lg font-medium text-gray-700">
                  ชื่ออาหาร:
                </label>
                <input
                  type="text"
                  id="foodName"
                  value={foodName}
                  onChange={(e) => setFoodName(e.target.value)}
                  className="block w-full px-4 py-3 mt-1 text-lg border border-gray-300 shadow-sm rounded-xl focus:ring-teal-500 focus:border-teal-500"
                  placeholder="เช่น ผัดไทย, ต้มยำกุ้ง"
                  required
                />
              </div>
              <div>
                <label htmlFor="foodImage" className="block mb-2 text-lg font-medium text-gray-700">
                  URL รูปภาพ:
                </label>
                <input
                  type="url"
                  id="foodImage"
                  value={foodImage}
                  onChange={(e) => setFoodImage(e.target.value)}
                  className="block w-full px-4 py-3 mt-1 text-lg border border-gray-300 shadow-sm rounded-xl focus:ring-teal-500 focus:border-teal-500"
                  placeholder="https://example.com/padthai.jpg"
                  required
                />
              </div>
              <div>
                <label htmlFor="foodTags" className="block mb-2 text-lg font-medium text-gray-700">
                  แท็ก (คั่นด้วยคอมม่า):
                </label>
                <input
                  type="text"
                  id="foodTags"
                  value={foodTags}
                  onChange={(e) => setFoodTags(e.target.value)}
                  className="block w-full px-4 py-3 mt-1 text-lg border border-gray-300 shadow-sm rounded-xl focus:ring-teal-500 focus:border-teal-500"
                  placeholder="เช่น อาหารจานเดียว, เส้น, ยอดนิยม, ทั่วไป, อาหารราดหน้า"
                />
              </div>
              <div className="flex justify-center space-x-4">
                <button
                  type="submit"
                  className="px-8 py-3 font-bold text-white transition duration-300 ease-in-out transform bg-teal-600 shadow-lg rounded-xl hover:bg-teal-700 focus:outline-none focus:ring-2 focus:ring-teal-500 focus:ring-offset-2 hover:scale-105"
                >
                  {editingFoodId ? 'บันทึกการแก้ไข' : 'เพิ่มเมนูอาหาร'}
                </button>
                {editingFoodId && (
                  <button
                    type="button"
                    onClick={() => {
                      setEditingFoodId(null);
                      setFoodName('');
                      setFoodImage('');
                      setFoodTags('');
                      showMessage('ยกเลิกการแก้ไข', 'info');
                    }}
                    className="px-8 py-3 font-bold text-white transition duration-300 ease-in-out transform bg-gray-400 shadow-lg rounded-xl hover:bg-gray-500 focus:outline-none focus:ring-2 focus:ring-gray-300 focus:ring-offset-2 hover:scale-105"
                  >
                    ยกเลิก
                  </button>
                )}
              </div>
            </form>
          </section>

          {/* Food Items Grid (user's private list) with Category Filter */}
          <section className="max-w-6xl mx-auto">
            <h2 className="mb-6 text-3xl font-bold text-center text-teal-700">รายการเมนูอาหารส่วนตัวของคุณ</h2>
            {/* Category Filter */}
            <div className="mb-6 text-center">
              <label htmlFor="category-filter" className="block mb-2 text-lg font-medium text-gray-700">
                กรองตามหมวดหมู่:
              </label>
              <select
                id="category-filter"
                value={selectedCategory}
                onChange={(e) => setSelectedCategory(e.target.value)}
                className="block w-full max-w-xs px-4 py-3 mx-auto mt-1 text-lg border border-gray-300 shadow-sm rounded-xl focus:ring-teal-500 focus:border-teal-500"
              >
                <option value="ทั้งหมด">ทั้งหมด</option>
                <option value="อาหารราดหน้า">อาหารราดหน้า</option>
                <option value="ทั่วไป">ทั่วไป</option>
                <option value="เส้น">เส้น</option>
              </select>
            </div>

            {filteredFoodItems.length === 0 ? (
              <p className="mt-8 text-xl text-center text-gray-600">
                ไม่พบเมนูอาหารในหมวดหมู่นี้ ลองเพิ่มเมนูใหม่ดูสิ!
              </p>
            ) : (
              <div className="grid grid-cols-1 gap-8 sm:grid-cols-2 lg:grid-cols-3">
                {filteredFoodItems.map((food) => (
                  <div
                    key={food.id}
                    className="overflow-hidden transition duration-300 ease-in-out transform bg-white border border-teal-200 shadow-xl rounded-2xl hover:scale-105"
                  >
                    <img
                      src={food.image}
                      alt={food.name}
                      className="object-cover w-full h-48 rounded-t-2xl"
                      onError={(e) => {
                        e.target.onerror = null;
                        e.target.src = `https://placehold.co/400x300/CCCCCC/000000?text=Image+Not+Found`;
                      }}
                    />
                    <div className="p-6">
                      <h3 className="mb-3 text-2xl font-semibold text-gray-800">{food.name}</h3>
                      <div className="flex flex-wrap gap-2 mb-4">
                        {food.tags.map((tag, index) => (
                          <span
                            key={index}
                            className="px-3 py-1 text-sm font-medium text-teal-700 bg-teal-100 rounded-full shadow-md"
                          >
                            {tag}
                          </span>
                        ))}
                      </div>
                      <div className="flex justify-end space-x-3">
                        <button
                          onClick={() => handleEditFood(food.id)}
                          className="px-5 py-2 text-white transition duration-200 ease-in-out bg-blue-500 rounded-lg shadow-md hover:bg-blue-600 focus:outline-none focus:ring-2 focus:ring-blue-400 focus:ring-offset-2"
                        >
                          แก้ไข
                        </button>
                        <button
                          onClick={() => handleDeleteFood(food.id)}
                          className="px-5 py-2 text-white transition duration-200 ease-in-out bg-red-500 rounded-lg shadow-md hover:bg-red-600 focus:outline-none focus:ring-2 focus:ring-red-400 focus:ring-offset-2"
                        >
                          ลบ
                        </button>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </section>
        </>
      )}

      {currentPage === 'vote' && (
        <section className="max-w-6xl p-8 mx-auto mb-10 bg-white border border-teal-200 shadow-xl rounded-2xl">
          <h2 className="mb-6 text-3xl font-bold text-center text-teal-700">เมนูประจำวัน</h2>
          {dailyMenu.status === 'voting' && (
            <div>
              <p className="mb-4 text-xl font-semibold text-center text-gray-700">
                กำลังเปิดโหวตเมนูประจำวัน!
                {dailyMenu.votedUsers && dailyMenu.votedUsers[userId] ? ' (คุณโหวตแล้ว)' : ''}
              </p>
              <div className="grid grid-cols-1 gap-6 md:grid-cols-2 lg:grid-cols-3">
                {dailyMenu.voteOptions.map((option) => (
                  <div key={option.foodItemId} className="flex flex-col items-center p-4 shadow-md bg-gray-50 rounded-xl">
                    <img
                      src={option.image}
                      alt={option.name}
                      className="object-cover w-full h-32 mb-3 rounded-lg"
                      onError={(e) => { e.target.onerror = null; e.target.src = `https://placehold.co/400x300/CCCCCC/000000?text=Image+Not+Found`; }}
                    />
                    <h3 className="mb-2 text-xl font-semibold">{option.name}</h3>
                    <p className="mb-4 text-lg text-teal-600">คะแนนโหวต: {option.votes}</p>
                    <button
                      onClick={() => handleVote(option.foodItemId)}
                      disabled={dailyMenu.votedUsers && dailyMenu.votedUsers[userId]}
                      className="px-6 py-2 text-white transition bg-blue-500 rounded-lg shadow-md hover:bg-blue-600 disabled:opacity-50 disabled:cursor-not-allowed"
                    >
                      โหวต
                    </button>
                  </div>
                ))}
              </div>
            </div>
          )}

          {(dailyMenu.status === 'closed' || dailyMenu.status === 'admin_set') && winningFood && (
            <div className="text-center">
              <p className="mb-4 text-2xl font-bold text-teal-700">
                เมนูประจำวันนี้คือ:
              </p>
              <div className="inline-block p-6 shadow-lg bg-teal-50 rounded-xl">
                <img
                  src={winningFood.image}
                  alt={winningFood.name}
                  className="object-cover w-64 h-48 mx-auto mb-4 rounded-lg"
                  onError={(e) => { e.target.onerror = null; e.target.src = `https://placehold.co/400x300/CCCCCC/000000?text=Image+Not+Found`; }}
                />
                <h3 className="text-3xl font-extrabold text-teal-800">{winningFood.name}</h3>
                <div className="flex flex-wrap justify-center gap-2 mt-3">
                  {winningFood.tags && winningFood.tags.map((tag, index) => (
                    <span
                      key={index}
                      className="px-3 py-1 text-sm font-medium text-teal-700 bg-teal-200 rounded-full"
                    >
                      {tag}
                    </span>
                  ))}
                </div>
              </div>
              {dailyMenu.status === 'closed' && (
                <p className="mt-4 text-gray-600">
                  (มาจากผลโหวต)
                </p>
              )}
              {dailyMenu.status === 'admin_set' && (
                <p className="mt-4 text-gray-600">
                  (ตั้งค่าโดยแอดมิน)
                </p>
              )}
            </div>
          )}

          {dailyMenu.status === 'idle' && (
            <p className="text-xl text-center text-gray-600">
              ยังไม่มีการตั้งค่าเมนูประจำวัน หรือกำลังรอแอดมินเริ่มการโหวต
            </p>
          )}
        </section>
      )}

      {currentPage === 'admin' && (
        <section className="max-w-3xl p-8 mx-auto mb-10 bg-white border border-teal-200 shadow-xl rounded-2xl">
          <h2 className="mb-6 text-3xl font-bold text-center text-teal-700">แผงควบคุมแอดมิน</h2>

          {!isAdmin ? (
            <div className="space-y-4 text-center">
              <p className="text-lg text-gray-700">กรุณาเข้าสู่ระบบแอดมิน</p>
              <input
                type="password"
                value={adminPasswordInput}
                onChange={(e) => setAdminPasswordInput(e.target.value)}
                className="block w-full max-w-xs px-4 py-3 mx-auto mt-1 text-lg border border-gray-300 shadow-sm rounded-xl focus:ring-teal-500 focus:border-teal-500"
                placeholder="รหัสผ่านแอดมิน"
              />
              <button
                onClick={handleAdminLogin}
                className="px-8 py-3 font-bold text-white transition duration-300 ease-in-out transform bg-teal-600 shadow-lg rounded-xl hover:bg-teal-700 focus:outline-none focus:ring-2 focus:ring-teal-500 focus:ring-offset-2 hover:scale-105"
              >
                เข้าสู่ระบบ
              </button>
            </div>
          ) : (
            <>
              <div className="mb-4 text-right">
                <button
                  onClick={handleAdminLogout}
                  className="px-4 py-2 text-sm font-bold text-white transition duration-300 bg-red-500 rounded-lg hover:bg-red-600"
                >
                  ออกจากระบบ
                </button>
              </div>

              {/* Selected Items for Voting Display */}
              <div className="p-4 mb-6 border rounded-lg bg-teal-50">
                <h3 className="mb-2 text-xl font-semibold text-teal-800">เมนูที่เลือกสำหรับโหวต ({adminVoteSelections.length}/5):</h3>
                {adminVoteSelections.length === 0 ? (
                  <p className="text-gray-600">ยังไม่ได้เลือกเมนู</p>
                ) : (
                  <div className="flex flex-wrap gap-2">
                    {adminVoteSelections.map(food => (
                      <span key={food.id} className="flex items-center px-3 py-1 text-sm font-medium text-teal-800 bg-teal-200 rounded-full">
                        {food.name}
                        <button
                          onClick={() => toggleAdminVoteSelection(food)}
                          className="ml-2 font-bold text-teal-700 hover:text-red-700"
                        >
                          &times;
                        </button>
                      </span>
                    ))}
                  </div>
                )}
                <button
                  onClick={handleStartVoting}
                  disabled={adminVoteSelections.length !== 5 || dailyMenu.status === 'voting'}
                  className="w-full px-6 py-3 mt-4 font-bold text-white transition duration-300 ease-in-out transform bg-teal-600 shadow-lg rounded-xl hover:bg-teal-700 disabled:opacity-50 disabled:cursor-not-allowed hover:scale-105"
                >
                  {dailyMenu.status === 'voting' ? 'กำลังโหวต...' : 'เริ่มการโหวต'}
                </button>
              </div>

              {/* All Food Items for Selection with Category Filter */}
              <div className="mb-6">
                <h3 className="mb-4 text-xl font-semibold text-gray-700">เลือกเมนูจากรายการทั้งหมด:</h3>
                {/* Admin Category Filter */}
                <div className="mb-4 text-center">
                  <label htmlFor="admin-category-filter" className="block mb-2 text-lg font-medium text-gray-700">
                    กรองตามหมวดหมู่:
                  </label>
                  <select
                    id="admin-category-filter"
                    value={selectedAdminCategory}
                    onChange={(e) => setSelectedAdminCategory(e.target.value)}
                    className="block w-full max-w-xs px-4 py-3 mx-auto mt-1 text-lg border border-gray-300 shadow-sm rounded-xl focus:ring-teal-500 focus:border-teal-500"
                  >
                    <option value="ทั้งหมด">ทั้งหมด</option>
                    <option value="อาหารราดหน้า">อาหารราดหน้า</option>
                    <option value="ทั่วไป">ทั่วไป</option>
                    <option value="เส้น">เส้น</option>
                  </select>
                </div>

                <div className="grid grid-cols-1 gap-4 p-2 overflow-y-auto border rounded-lg sm:grid-cols-2 max-h-96 bg-gray-50">
                  {filteredAdminFoodItems.map((food) => { // Use filteredAdminFoodItems here
                    const isSelected = adminVoteSelections.some(item => item.id === food.id);
                    const isDisabled = adminVoteSelections.length >= 5 && !isSelected;
                    return (
                      <div
                        key={food.id}
                        className={`p-3 border rounded-lg flex items-center justify-between ${
                          isSelected ? 'bg-teal-100 border-teal-400' : 'bg-white border-gray-200'
                        } ${isDisabled ? 'opacity-50 cursor-not-allowed' : ''}`}
                      >
                        <div className="flex items-center">
                          <img
                            src={food.image}
                            alt={food.name}
                            className="object-cover w-12 h-12 mr-3 rounded-md"
                            onError={(e) => { e.target.onerror = null; e.target.src = `https://placehold.co/400x300/CCCCCC/000000?text=NF`; }}
                          />
                          <span className="font-medium text-gray-800">{food.name}</span>
                        </div>
                        <button
                          onClick={() => toggleAdminVoteSelection(food)}
                          disabled={isDisabled}
                          className={`px-4 py-2 rounded-lg text-white font-semibold transition ${
                            isSelected ? 'bg-red-500 hover:bg-red-600' : 'bg-blue-500 hover:bg-blue-600'
                          } disabled:opacity-50 disabled:cursor-not-allowed`}
                        >
                          {isSelected ? 'ลบออก' : 'เพิ่มเข้าโหวต'}
                        </button>
                      </div>
                    );
                  })}
                </div>
              </div>

              {/* Close Voting */}
              {dailyMenu.status === 'voting' && (
                <div className="pt-4 mb-6 border-t border-gray-200">
                  <h3 className="mb-4 text-xl font-semibold text-gray-700">ปิดการโหวต:</h3>
                  <button
                    onClick={handleCloseVoting}
                    className="w-full px-6 py-3 font-bold text-white transition duration-300 ease-in-out transform bg-orange-500 shadow-lg rounded-xl hover:bg-orange-600 disabled:opacity-50 disabled:cursor-not-allowed hover:scale-105"
                  >
                    ปิดการโหวต
                  </button>
                </div>
              )}

              {/* Admin Direct Select Food */}
              <div className="pt-4 border-t border-gray-200">
                <h3 className="mb-4 text-xl font-semibold text-gray-700">ตั้งค่าเมนูประจำวันโดยตรง:</h3>
                <select
                  value={adminDirectSelectFoodId}
                  onChange={(e) => setAdminDirectSelectFoodId(e.target.value)}
                  className="block w-full px-4 py-3 mt-1 text-lg border border-gray-300 shadow-sm rounded-xl focus:ring-teal-500 focus:border-teal-500"
                >
                  <option value="">-- เลือกเมนู --</option>
                  {foodItems.map(food => (
                    <option key={food.id} value={food.id}>{food.name}</option>
                  ))}
                </select>
                <button
                  onClick={handleAdminSetFood}
                  disabled={!adminDirectSelectFoodId}
                  className="w-full px-6 py-3 mt-4 font-bold text-white transition duration-300 ease-in-out transform bg-purple-600 shadow-lg rounded-xl hover:bg-purple-700 disabled:opacity-50 disabled:cursor-not-allowed hover:scale-105"
                >
                  ตั้งค่าเมนูประจำวัน
                </button>
              </div>
            </>
          )}
        </section>
      )}
    </div>
  );
};

export default App;
